ARAGON_CONTRACT_ADDR = "0xa117000000f279d81a1d3cc75430faa017fa5a2e"

CONTRACT = W3.eth.contract(
    address=W3.toChecksumAddress(ARAGON_CONTRACT_ADDR), abi=ABI)


ARAGON_CONTRACT_PARAMETERS = {
    "address": ARAGON_CONTRACT_ADDR,
    "topics": ["0xddf252ad1be2c89b69c2b068fc378daa952ba7f163c4a11628f55a4df523b3ef"],
}


def ARAGON_Parser(payload: dict) -> str:
    value = decode_single(
        "(uint256)",
        bytearray.fromhex(
            payload["params"]["result"]["data"][2:]),
    )[0]
    # print(value)
    from_user = payload["params"]["result"]["topics"][1][26:]
    to_user = payload["params"]["result"]["topics"][2][26:]
    msg = f"User 0x{from_user} transfer {value} to 0x{to_user}, TransactionHash:{payload['params']['result']['transactionHash']}"
    # print(msg)
    return {
        "title": "Transfer event",
        "content": msg,
    }
